import React, { useState } from 'react';
import './App.css';

function App() {

  const [todoText, setTodoText] = useState("");
  const [incompleteTodos, setIncompleteTodos] = useState<Todo[]>([]);
  const [completeTodos, setCompleteTodos] = useState<Todo[]>([]);

  type Todo = {
    inputValue: string;
    id: number;
    checked: boolean;
  };

  const handleChange= (e: React.ChangeEvent<HTMLInputElement>) => {
    setTodoText(e.target.value);
  }

  const handleSubmit = (e: { preventDefault: () => void; }) => {
    e.preventDefault();

    if(todoText === "") {
      return;
    }

    const newTodo: Todo = {
      inputValue: todoText,
      id: incompleteTodos.length + 1,
      checked: false,
    };

    setIncompleteTodos([...incompleteTodos, newTodo]);
  }

  return (
    <div className='App'>
      <section><p>競走馬リスト</p></section>
      <div className='formArea'>
        <form>
          <input type="text" placeholder="ここに入力" value={todoText} onChange={handleChange} className='umaName' />
          <input type="submit" onSubmit={handleSubmit} value="＋" className='umaButton' />
        </form>
      </div>
      <div className='IncompleteArea'>
        <p className='title'>まだ撮ってない馬</p>
        <ul>
          {incompleteTodos.map((todo) => {
            return (
              <li key={todo.id}>
                <p>{todo.inputValue}</p>
                <button>完了</button>
                <button>削除</button>
              </li>
            )
          })}
        </ul>
      </div>
      <div className='CompleteArea'>
        <p className='title'>もう撮った馬</p>
        <ul>
          {completeTodos.map((todo) => {
            return (
              <li key={todo.id}>
                <p>{todo.inputValue}</p>
                <button>戻す</button></li>
            )
          })}
        </ul>
      </div>
    </div>
  )
}

export default App;
